import React from "react";
import styled from "@emotion/styled";

const QuoteContainer = styled.div`
  padding: 3rem;
  border-radius: 0.5rem;
  background-color: #fff;
  max-width: 800px;
  @media (min-width: 992px) {
    margin-top: 10rem;
  }

  h1 {
    font-family: Arial, Helvetica, sans-serif;
    text-align: center;
    position: relative;
    padding-left: 4rem;

    &::before {
      content: open-quote;
      font-size: 10rem;
      color: black;
      position: absolute;
      left: -1rem;
      top: -2rem;
    }
  }
`;

const Quote = ({ quote }) => {
  if (!quote) return null;
  return (
    <QuoteContainer>
      <h1>{quote.quote}</h1>
      <p>{quote.author}</p>
    </QuoteContainer>
  );
};

export default Quote;
